package xyz.connorchickenway.stella.tab.skin;

public class Skin {

    private final String value, signature;

    private Skin(String value, String signature) {
        this.value = value;
        this.signature = signature;
    }

    public String getValue() {
        return value;
    }

    public String getSignature() {
        return signature;
    }

    public static Skin of(String value, String signature) {
        return new Skin(value, signature);
    }

    @Override
    public boolean equals(Object o) {
        if (!(o instanceof Skin)) {
            return false;
        }
        if (this == o) {
            return true;
        }
        Skin tmpSkin = (Skin) o;
        return this.getSignature().equalsIgnoreCase(tmpSkin.getSignature()) &&
                this.getValue().equalsIgnoreCase(tmpSkin.getValue());
    }

    public static final Skin DEFAULT = of(
            "",
            ""
    );

}
